import * as React from 'react';
import { ChevronLeftIcon, ChevronRightIcon } from "@radix-ui/react-icons"
import { DayPicker } from 'react-day-picker';
import { cn } from '@/lib/utils';
import buttonStyles from '@/components/ui/button/button.module.css';
import styles from './calendar.module.css';

export type CalendarProps = React.ComponentProps<typeof DayPicker>;

function Calendar({
  className,
  classNames,
  showOutsideDays = true,
  ...props
}: CalendarProps) {
  return (
    <DayPicker
      showOutsideDays={showOutsideDays}
      className={cn(styles.calendar, className)}
      classNames={{
        months: styles.calendarMonths,
        month: styles.calendarMonth,
        caption: styles.calendarCaption,
        caption_label: styles.calendarCaptionLabel,
        nav: cn(
          buttonStyles.buttonOutline,
          styles.calendarNac
        ),
        nav_button: styles.calendarNavButton,
        nav_button_previous: styles.calendarNavButtonPrevious,
        nav_button_next: styles.calendarNavButtonNext,
        table: styles.calendarTable,
        head_row: styles.calendarHeadRow,
        head_cell: styles.calendarHeadCell,
        row: styles.calendarRow,
        cell: styles.calendarCell,
        day: cn(
          buttonStyles.buttonGhost,
          styles.calendarDay
        ),
        day_range_end: styles.calendarDayRangeEnd,
        day_selected: styles.calendarDaySelected,
        day_today: styles.calendarDayToday,
        day_outside: styles.calendarDayOutside,
        day_disabled: styles.calendarDayDisabled,
        day_range_middle: styles.calendarDayRangeMiddle,
        day_hidden: styles.calendarDayHidden,
        ...classNames,
      }}
      components={{
        IconLeft: ({ ...props }) => (
          <ChevronLeftIcon className={styles.calendarChevronLeft} {...props} />
        ),
        IconRight: ({ ...props }) => (
          <ChevronRightIcon className={styles.calendarChevronLeft} {...props} />
        ),
      }}
      {...props}
    />
  );
}
Calendar.displayName = 'Calendar';

export { Calendar };

.calendar {
  padding: 0.75rem;
}

.calendarMonths {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

@media (min-width: 640px) {
  .calendarMonths {
    flex-direction: row;
    gap: 1rem 1rem;
  }
}

.calendarMonth {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.calendarCaption {
  display: flex;
  justify-content: center;
  align-items: center;
  padding-top: 0.25rem;
  position: relative;
}

.calendarCaptionLabel {
  font-size: 0.875rem;
  font-weight: 500;
}

.calendarNav {
  display: flex;
  align-items: center;
  gap: 0.25rem;
}

.calendarNavButton {
  height: 1.75rem;
  width: 1.75rem;
  background: transparent;
  padding: 0;
  opacity: 0.5;
  transition: opacity 0.2s;
  border-radius: var(--radius);
  border: 1px solid hsl(var(--border));
}

.calendarNavButton:hover {
  opacity: 1;
}

.calendarNavButtonPrevious {
  position: absolute;
  left: 0.25rem;
}

.calendarNavButtonNext {
  position: absolute;
  right: 0.25rem;
}

.calendarTable {
  width: 100%;
  border-collapse: collapse;
}

.calendarHeadRow {
  display: flex;
}

.calendarHeadCell {
  color: hsl(var(--muted-foreground));
  border-radius: var(--radius);
  width: 2.25rem;
  font-weight: 400;
  font-size: 0.8rem;
}

.calendarRow {
  display: flex;
  width: 100%;
  margin-top: 0.5rem;
}

.calendarCell {
  height: 2.25rem;
  width: 2.25rem;
  text-align: center;
  font-size: 0.875rem;
  padding: 0;
  position: relative;
}

.day-range-end {
  border-radius: 0 0.5rem 0.5rem 0;
}

.calendarDay {
  height: 2.25rem;
  width: 2.25rem;
  padding: 0;
  font-weight: 400;
  background: none;
  border: none;
  border-radius: var(--radius);
  opacity: 1;
  cursor: pointer;
  transition:
    background 0.2s,
    color 0.2s,
    opacity 0.2s;
}

.calendarDay[aria-selected='true'] {
  opacity: 1;
}

.day-selected {
  background: hsl(var(--primary));
  color: hsl(var(--primary-foreground));
}

.day-selected:hover,
.day-selected:focus {
  background: hsl(var(--primary));
  color: hsl(var(--primary-foreground));
}

.day-today {
  background: hsl(var(--accent));
  color: hsl(var(--accent-foreground));
}

.day-outside {
  color: hsl(var(--muted-foreground));
  opacity: 0.5;
}

.day-outside[aria-selected='true'] {
  background: hsl(var(--accent) / 0.5);
  color: hsl(var(--muted-foreground));
  opacity: 0.3;
}

.day-disabled {
  color: hsl(var(--muted-foreground));
  opacity: 0.5;
}

.day-range-middle[aria-selected='true'] {
  background: hsl(var(--accent));
  color: hsl(var(--accent-foreground));
}

.day-hidden {
  visibility: hidden;
}

.calendarChevronLeft {
  width: 1rem;
  height: 1rem;
}

.calendarChevronRight {
  width: 1rem;
  height: 1rem;
}

