"use client";

import { useTheme } from "next-themes";
import { Toaster as Sonner } from "sonner";
import { cn } from "@/lib/utils";
import styles from "./sooner.module.css";

type ToasterProps = React.ComponentProps<typeof Sonner>;

const Toaster = ({ ...props }: ToasterProps) => {
  const { theme = "system" } = useTheme();

  return (
    <Sonner
      theme={theme as ToasterProps["theme"]}
      className="toaster"
      toastOptions={{
        classNames: {
          toast: cn('toast', styles.toast),
          description: styles.toastDescription,
          actionButton: styles.toastActionButton,
          cancelButton: styles.toastCancelButton,
        },
      }}
      {...props}
    />
  )
};

export { Toaster };

.toast {
  background: hsl(var(--background));
  color: hsl(var(--foreground));
  border: 1px solid hsl(var(--border));
  box-shadow:
    0 4px 24px 0 rgba(0, 0, 0, 0.13),
    0 1.5px 4px 0 rgba(0, 0, 0, 0.09);
  border-radius: var(--radius);
}

.toastDescription {
  color: hsl(var(--muted-foreground));
}

.toastActionButton {
  background: hsl(var(--primary));
  color: hsl(var(--primary-foreground));
  border: none;
  border-radius: var(--radius);
  cursor: pointer;
}

.toastCancelButton {
  background: hsl(var(--muted));
  color: hsl(var(--muted-foreground));
  border: none;
  border-radius: var(--radius);
  cursor: pointer;
}
