'use client';

import * as React from 'react';
import * as TogglePrimitive from '@radix-ui/react-toggle';
import { cva, type VariantProps } from 'class-variance-authority';
import { cn } from '@/lib/utils';
import styles from './toggle.module.css';

const toggleVariants = cva(styles.toggleBase, {
  variants: {
    variant: {
      default: styles.toggleDefault,
      outline: styles.toggleOutline,
    },
    size: {
      default: styles.toggleSizeDefault,
      sm: styles.toggleSizeSm,
      lg: styles.toggleSizeLg,
    },
  },
  defaultVariants: {
    variant: 'default',
    size: 'default',
  },
});

const Toggle = React.forwardRef<
  React.ElementRef<typeof TogglePrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof TogglePrimitive.Root> &
    VariantProps<typeof toggleVariants>
>(({ className, variant, size, ...props }, ref) => (
  <TogglePrimitive.Root
    ref={ref}
    className={cn(toggleVariants({ variant, size, className }))}
    {...props}
  />
));
Toggle.displayName = TogglePrimitive.Root.displayName;

export { Toggle, toggleVariants };

.toggleBase {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border-radius: calc(var(--radius) - 2px);
  font-size: 0.875rem;
  line-height: 1.25rem;
  font-weight: 500;
  transition-property:
    color, background-color, border-color, text-decoration-color, fill, stroke;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  transition-duration: 150ms;
}
.toggleBase:hover {
  background-color: hsl(var(--muted));
  color: hsl(var(--muted-foreground));
}
.toggleBase:focus-visible {
  outline: 2px solid transparent;
  outline-offset: 2px;
  --tw-ring-offset-shadow: var(--tw-ring-inset) 0 0 0
    var(--tw-ring-offset-width) var(--tw-ring-offset-color);
  --tw-ring-shadow: var(--tw-ring-inset) 0 0 0
    calc(1px + var(--tw-ring-offset-width)) var(--tw-ring-color);
  box-shadow:
    var(--tw-ring-offset-shadow), var(--tw-ring-shadow),
    var(--tw-shadow, 0 0 #0000);
  --tw-ring-color: hsl(var(--ring));
}
.toggleBase:disabled {
  pointer-events: none;
  opacity: 0.5;
}
.toggleBase[data-state="on"] {
  background-color: hsl(var(--accent));
  color: hsl(var(--accent-foreground));
}
.toggleDefault {
  background-color: transparent;
}
.toggleOutline {
  border-width: 1px;
  border-color: hsl(var(--input));
  background-color: transparent;
  --tw-shadow: 0 1px 2px 0 rgb(0 0 0 / 0.05);
  --tw-shadow-colored: 0 1px 2px 0 var(--tw-shadow-color);
  box-shadow:
    var(--tw-ring-offset-shadow, 0 0 #0000), var(--tw-ring-shadow, 0 0 #0000),
    var(--tw-shadow);
}
.toggleOutline:hover {
  background-color: hsl(var(--accent));
  color: hsl(var(--accent-foreground));
}
.toggleSizeDefault {
  height: 2.25rem;
  padding-left: 0.75rem;
  padding-right: 0.75rem;
}
.toggleSizeSm {
  height: 2rem;
  padding-left: 0.5rem;
  padding-right: 0.5rem;
}
.toggleSizeLg {
  height: 2.5rem;
  padding-left: 0.75rem;
  padding-right: 0.75rem;
}

