'use client';

import * as React from 'react';
import * as SheetPrimitive from '@radix-ui/react-dialog';
import { cva, type VariantProps } from 'class-variance-authority';
import { X } from 'lucide-react';
import { cn } from '@/lib/utils';
import styles from './sheet.module.css';

const Sheet = SheetPrimitive.Root;

const SheetTrigger = SheetPrimitive.Trigger;

const SheetClose = SheetPrimitive.Close;

const SheetPortal = SheetPrimitive.Portal;

const SheetOverlay = React.forwardRef<
  React.ElementRef<typeof SheetPrimitive.Overlay>,
  React.ComponentPropsWithoutRef<typeof SheetPrimitive.Overlay>
>(({ className, ...props }, ref) => (
  <SheetPrimitive.Overlay
    className={cn(styles.sheetOverlay, className)}
    {...props}
    ref={ref}
  />
));
SheetOverlay.displayName = SheetPrimitive.Overlay.displayName;

const sheetVariants = cva(styles.sheetBase, {
  variants: {
    side: {
      top: styles.sheetTop,
      bottom: styles.sheetBottom,
      left: styles.sheetLeft,
      right: styles.sheetRight,
    },
  },
  defaultVariants: {
    side: 'right',
  },
});
interface SheetContentProps
  extends React.ComponentPropsWithoutRef<typeof SheetPrimitive.Content>,
    VariantProps<typeof sheetVariants> {}

const SheetContent = React.forwardRef<
  React.ElementRef<typeof SheetPrimitive.Content>,
  SheetContentProps
>(({ side = 'right', className, children, ...props }, ref) => (
  <SheetPortal>
    <SheetOverlay />
    <SheetPrimitive.Content
      ref={ref}
      className={cn(sheetVariants({ side }), className)}
      {...props}
    >
      {children}
      <SheetPrimitive.Close className={styles.sheetContent}>
        <X className={styles.sheetContentX} />
        <span className={styles.sheetContentSpan}>Close</span>
      </SheetPrimitive.Close>
    </SheetPrimitive.Content>
  </SheetPortal>
));
SheetContent.displayName = SheetPrimitive.Content.displayName;

const SheetHeader = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div className={cn(styles.sheetHeader, className)} {...props} />
);
SheetHeader.displayName = 'SheetHeader';

const SheetFooter = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div className={cn(styles.sheetFooter, className)} {...props} />
);
SheetFooter.displayName = 'SheetFooter';

const SheetTitle = React.forwardRef<
  React.ElementRef<typeof SheetPrimitive.Title>,
  React.ComponentPropsWithoutRef<typeof SheetPrimitive.Title>
>(({ className, ...props }, ref) => (
  <SheetPrimitive.Title
    ref={ref}
    className={cn(styles.sheetTitle, className)}
    {...props}
  />
));
SheetTitle.displayName = SheetPrimitive.Title.displayName;

const SheetDescription = React.forwardRef<
  React.ElementRef<typeof SheetPrimitive.Description>,
  React.ComponentPropsWithoutRef<typeof SheetPrimitive.Description>
>(({ className, ...props }, ref) => (
  <SheetPrimitive.Description
    ref={ref}
    className={cn(styles.sheetDescription, className)}
    {...props}
  />
));
SheetDescription.displayName = SheetPrimitive.Description.displayName;

export {
  Sheet,
  SheetPortal,
  SheetOverlay,
  SheetTrigger,
  SheetClose,
  SheetContent,
  SheetHeader,
  SheetFooter,
  SheetTitle,
  SheetDescription,
};

.sheetBase {
  position: fixed;
  z-index: 50;
  gap: 1rem;
  background-color: hsl(var(--background));
  padding: 1.5rem;
  --tw-shadow:
    0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
  --tw-shadow-colored:
    0 10px 15px -3px var(--tw-shadow-color),
    0 4px 6px -4px var(--tw-shadow-color);
  box-shadow:
    var(--tw-ring-offset-shadow, 0 0 #0000), var(--tw-ring-shadow, 0 0 #0000),
    var(--tw-shadow);
  transition-property:
    color, background-color, border-color, text-decoration-color, fill, stroke,
    opacity, box-shadow, transform, filter, backdrop-filter;
  transition-duration: 150ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  animation-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}
.sheetBase[data-state="open"] {
  animation: enter 0.15s cubic-bezier(0.16, 1, 0.3, 1) both;
  transition-duration: 500ms;
  animation-name: enter;
  animation-duration: 150ms;
  --tw-enter-opacity: initial;
  --tw-enter-scale: initial;
  --tw-enter-rotate: initial;
  --tw-enter-translate-x: initial;
  --tw-enter-translate-y: initial;
  animation-duration: 500ms;
}
.sheetBase[data-state="closed"] {
  animation: exit 0.15s cubic-bezier(0.16, 1, 0.3, 1) both;
  transition-duration: 300ms;
  animation-name: exit;
  --tw-exit-opacity: initial;
  --tw-exit-scale: initial;
  --tw-exit-rotate: initial;
  --tw-exit-translate-x: initial;
  --tw-exit-translate-y: initial;
  animation-duration: 300ms;
}
.sheetTop {
  left: 0px;
  right: 0px;
  top: 0px;
  border-bottom-width: 1px;
}
.sheetTop[data-state="closed"] {
  --tw-exit-translate-y: -100%;
}
.sheetTop[data-state="open"] {
  --tw-enter-translate-y: -100%;
}
.sheetBottom {
  left: 0px;
  right: 0px;
  bottom: 0px;
  border-top-width: 1px;
}
.sheetBottom[data-state="closed"] {
  --tw-exit-translate-y: 100%;
}
.sheetBottom[data-state="open"] {
  --tw-enter-translate-y: 100%;
}
.sheetLeft {
  top: 0px;
  bottom: 0px;
  left: 0px;
  height: 100%;
  width: 75%;
  border-right-width: 1px;
}
.sheetLeft[data-state="closed"] {
  --tw-exit-translate-x: -100%;
}
.sheetLeft[data-state="open"] {
  --tw-enter-translate-x: -100%;
}
@media (min-width: 640px) {
  .sheetLeft {
    max-width: 24rem;
  }
}
.sheetRight {
  top: 0px;
  bottom: 0px;
  right: 0px;
  height: 100%;
  width: 75%;
  border-left-width: 1px;
}
.sheetRight[data-state="closed"] {
  --tw-exit-translate-x: 100%;
}
.sheetRight[data-state="open"] {
  --tw-enter-translate-x: 100%;
}
@media (min-width: 640px) {
  .sheetRight {
    max-width: 24rem;
  }
}
.sheetOverlay {
  position: fixed;
  inset: 0px;
  z-index: 50;
  background-color: rgb(0 0 0 / 0.8);
}
.sheetOverlay[data-state="open"] {
  animation: enter 0.15s cubic-bezier(0.16, 1, 0.3, 1) both;
  animation-name: enter;
  animation-duration: 150ms;
  --tw-enter-opacity: initial;
  --tw-enter-scale: initial;
  --tw-enter-rotate: initial;
  --tw-enter-translate-x: initial;
  --tw-enter-translate-y: initial;
  --tw-enter-opacity: 0;
}
.sheetOverlay[data-state="closed"] {
  animation: exit 0.15s cubic-bezier(0.16, 1, 0.3, 1) both;
  animation-name: exit;
  animation-duration: 150ms;
  --tw-exit-opacity: initial;
  --tw-exit-scale: initial;
  --tw-exit-rotate: initial;
  --tw-exit-translate-x: initial;
  --tw-exit-translate-y: initial;
  --tw-exit-opacity: 0;
}
.sheetContent {
  position: absolute;
  right: 1rem;
  top: 1rem;
  border-radius: calc(var(--radius) - 4px);
  opacity: 0.7;
  --tw-ring-offset-color: hsl(var(--background));
  transition-property: opacity;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  transition-duration: 150ms;
}
.sheetContent:hover {
  opacity: 1;
}
.sheetContent:focus {
  outline: 2px solid transparent;
  outline-offset: 2px;
  --tw-ring-offset-shadow: var(--tw-ring-inset) 0 0 0
    var(--tw-ring-offset-width) var(--tw-ring-offset-color);
  --tw-ring-shadow: var(--tw-ring-inset) 0 0 0
    calc(2px + var(--tw-ring-offset-width)) var(--tw-ring-color);
  box-shadow:
    var(--tw-ring-offset-shadow), var(--tw-ring-shadow),
    var(--tw-shadow, 0 0 #0000);
  --tw-ring-color: hsl(var(--ring));
  --tw-ring-offset-width: 2px;
}
.sheetContent:disabled {
  pointer-events: none;
}
.sheetContent[data-state="open"] {
  background-color: hsl(var(--secondary));
}
.sheetContentX {
  height: 1rem;
  width: 1rem;
}
.sheetContentSpan {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border-width: 0;
}
.sheetHeader {
  display: flex;
  flex-direction: column;
  text-align: center;
}
.sheetHeader > :not([hidden]) ~ :not([hidden]) {
  --tw-space-y-reverse: 0;
  margin-top: calc(0.5rem * calc(1 - var(--tw-space-y-reverse)));
  margin-bottom: calc(0.5rem * var(--tw-space-y-reverse));
}
@media (min-width: 640px) {
  .sheetHeader {
    text-align: left;
  }
}
.sheetFooter {
  display: flex;
  flex-direction: column-reverse;
}
@media (min-width: 640px) {
  .sheetFooter {
    flex-direction: row;
    justify-content: flex-end;
  }
  .sheetFooter > :not([hidden]) ~ :not([hidden]) {
    --tw-space-x-reverse: 0;
    margin-right: calc(0.5rem * var(--tw-space-x-reverse));
    margin-left: calc(0.5rem * calc(1 - var(--tw-space-x-reverse)));
  }
}
.sheetTitle {
  font-size: 1.125rem;
  line-height: 1.75rem;
  font-weight: 600;
  color: hsl(var(--foreground));
}
.sheetDescription {
  font-size: 0.875rem;
  line-height: 1.25rem;
  color: hsl(var(--muted-foreground));
}
@keyframes enter {
  from {
    opacity: var(--tw-enter-opacity, 1);
    transform: translate3d(
        var(--tw-enter-translate-x, 0),
        var(--tw-enter-translate-y, 0),
        0
      )
      scale3d(
        var(--tw-enter-scale, 1),
        var(--tw-enter-scale, 1),
        var(--tw-enter-scale, 1)
      )
      rotate(var(--tw-enter-rotate, 0));
    opacity: var(--tw-enter-opacity, 1);
    transform: translate3d(
        var(--tw-enter-translate-x, 0),
        var(--tw-enter-translate-y, 0),
        0
      )
      scale3d(
        var(--tw-enter-scale, 1),
        var(--tw-enter-scale, 1),
        var(--tw-enter-scale, 1)
      )
      rotate(var(--tw-enter-rotate, 0));
  }
  0% {
    opacity: 0;
    transform: scale(0.95);
    opacity: 0;
    transform: scale(0.95);
  }
  100% {
    opacity: 1;
    transform: scale(1);
    opacity: 1;
    transform: scale(1);
  }
}
@keyframes exit {
  to {
    opacity: var(--tw-exit-opacity, 1);
    transform: translate3d(
        var(--tw-exit-translate-x, 0),
        var(--tw-exit-translate-y, 0),
        0
      )
      scale3d(
        var(--tw-exit-scale, 1),
        var(--tw-exit-scale, 1),
        var(--tw-exit-scale, 1)
      )
      rotate(var(--tw-exit-rotate, 0));
    opacity: var(--tw-exit-opacity, 1);
    transform: translate3d(
        var(--tw-exit-translate-x, 0),
        var(--tw-exit-translate-y, 0),
        0
      )
      scale3d(
        var(--tw-exit-scale, 1),
        var(--tw-exit-scale, 1),
        var(--tw-exit-scale, 1)
      )
      rotate(var(--tw-exit-rotate, 0));
  }
  0% {
    opacity: 1;
    transform: scale(1);
    opacity: 1;
    transform: scale(1);
  }
  100% {
    opacity: 0;
    transform: scale(0.95);
    opacity: 0;
    transform: scale(0.95);
  }
}
